import { createElement, useState, useEffect } from "react";
import {
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";
export default function LoggedIn() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "posts");

  const [author, setAuthor] = useState("");
  const [post, setPost] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);

  function addPost(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: theDate,
      author: currentUser?.email,
      post: post,
    });
  }

  function fetchPosts() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);

      setDatabaseItems(items);

      // console.log(postList + "hey");
    });
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  function deletePost(e) {
    e.preventDefault();

    doc(db, "posts", itemToDelete);
  }
  return (
    <div>
      <br />
      <br />
      <div>Hey: {currentUser?.email} </div>

      <form className="add">
        <label htmlFor="author">Author:</label>
        <input value={author} type="text" name="author" />
        <label htmlFor="post">Post:</label>
        <input
          value={post}
          onChange={(e) => setPost(e.target.value)}
          type="text"
          name="post"
          required
        />

        <button onClick={addPost}> Post</button>
      </form>
      <form className="delete">
        <label htmlFor="id">Document id:</label>
        <input type="text" name="id" required />

        <button onClick={deletePost}>Delete</button>
      </form>
      <div id="postlist">
        {" "}
        <h2>Post List</h2>
        <div>
          {dataBaseItems.map((database, id) => (
            <div className="post" key={database.id}>
              <p className="date">{database.date}</p>
              <p>
                {" "}
                {database.post} -<i>{database.author}</i>{" "}
              </p>
              <button
                onClick={() => {
                  setItemToDelete(database.id);
                  deletePost();
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
