import { createElement, useState, useEffect } from "react";
import {
  FieldValue,
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";
import RandomPost from "./makeRandomPost";
import { NavLink } from "react-router-dom";

export default function LoggedIn() {
  const db = getFirestore();

  const currentUser = useAuth();

  const colRef = collection(db, "posts");

  const [author, setAuthor] = useState("");
  const [post, setPost] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);

  function addPost(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: new Date(),
      displayDate: theDate,
      author: currentUser?.email,
      post: post,
    });
    setPost("");
    fetchPosts();
  }

  function fetchPosts() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);

      setDatabaseItems(items);
      dataBaseItems.sort((a, b) => (b.joinDate < a.joinDate ? 1 : -1));
    });
  }
  useEffect(() => {
    fetchPosts();
  }, []);

  function deletePost(e) {
    console.log("To be Deleted " + itemToDelete);
    if (itemToDelete != "") {
      console.log("Deleting");
      const docToDelete = doc(db, "posts", itemToDelete);
      deleteDoc(docToDelete).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchPosts();
    }
    // myDb.collection("posts").doc(itemToDelete).delete();
  }
  return (
    <div>
      <div>Hey: {currentUser?.email} </div>
      <ul>
        <li className="nav-link">
          <NavLink activestyle={{ color: "red" }} to="/">
            {" "}
            Home
          </NavLink>
        </li>
      </ul>
      <RandomPost />
    </div>
  );
}
