import { createElement, useState, useEffect } from "react";
import {
  FieldValue,
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";

export default function ShowsToWatch() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "currentShows");

  const [author, setAuthor] = useState("");
  const [show, setShow] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);

  function addShow(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: new Date(),
      displayDate: theDate,
      author: currentUser?.email,
      show: show,
    });
    setShow("");
    fetchShows();
  }

  function fetchShows() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);

      setDatabaseItems(items);
      dataBaseItems.sort((a, b) => (b.joinDate < a.joinDate ? 1 : -1));
    });
  }
  useEffect(() => {
    fetchShows();
  }, []);

  function deleteShow(e) {
    console.log("To be Deleted " + itemToDelete);
    if (itemToDelete != "") {
      console.log("Deleting");
      const docToDelete = doc(db, "shows", itemToDelete);
      deleteDoc(docToDelete).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchShows();
    }
  }
  return (
    <div id="showlist">
      <br />
      <br />

      <form className="add">
        <textarea
          rows="10"
          cols="70"
          value={show}
          onChange={(e) => setShow(e.target.value)}
          type="text"
          name="show"
          required
        />
        <br />
        <button onClick={addShow}> Add show</button>
      </form>

      <div>
        {" "}
        <h2>Show List</h2>
        <div>
          {dataBaseItems.map((database, id) => (
            <div className="insertedItem" key={database.id}>
              <p className="date">{database.displayDate}</p>
              <p> {database.show} </p>
              <p>
                Added by: <i>{database.author}</i>
              </p>
              <button
                onClick={(e) => {
                  setItemToDelete(database.id);
                  deleteShow();
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
