import { useState, useEffect } from "react";
import {
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
} from "firebase/firestore";
import { useAuth } from "./firebase";

export default function LoggedIn() {
  const db = getFirestore();
  const currentUser = useAuth();

  const colRef = collection(db, "posts");

  const [post, setPost] = useState("");
  const [author] = useState("");

  function addPost(e) {
    e.preventDefault();

    addDoc(colRef, {
      author: currentUser?.email,
      post: post,
    });
  }
  const [postList, setPostList] = useState([]);

  const FetchPosts = async () => {
    const apiCall = await fetch(getDocs(colRef));
    const snapshot = await apiCall.docs;
    setPostList(snapshot);
    console.log(postList);
  };

  //useEffect(() => {
  /* getDocs(colRef).then((snapshot) => {
      snapshot.docs.forEach((doc) => {
        postList.push({ ...doc.data(), id: doc.id });

        /*
        var z = document.createElement("li"); // is a node
        z.innerHTML =
          JSON.stringify(doc.data().author) +
          " " +
          JSON.stringify(doc.data().post);
        document.body.appendChild(z);

      });
    }); */
  // });

  function deletePost(e) {
    e.preventDefault();
  }
  return (
    <div>
      <body>
        <br />
        <br />
        <div>Hey: {currentUser?.email} </div>

        <form className="add">
          <label htmlFor="author">Author:</label>
          <input value={author} type="text" name="author" />
          <label htmlFor="post">Post:</label>
          <input
            value={post}
            onChange={(e) => setPost(e.target.value)}
            type="text"
            name="post"
            required
          />

          <button onClick={addPost}> Post</button>
        </form>
        <form className="delete">
          <label htmlFor="id">Document id:</label>
          <input type="text" name="id" required />

          <button onClick={deletePost}>Delete</button>
        </form>
        <div id="postlist">
          {" "}
          <h2>Post List</h2>
          {postList.map((doc) => (
            <ul>
              <li>{doc.author}</li>
              <li>{doc.post}</li>
            </ul>
          ))}
        </div>
      </body>
    </div>
  );
}
