import { useState, useEffect } from "react";
import {
  updateDoc,
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";

export default function Activities() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "activities");

  const [activeStatus, setActiveStatus] = useState(false);
  const [activity, setActivity] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);
  const [itemToUpdate, setItemToUpdate] = useState("");
  const [updating, setUpdating] = useState(false);
  const [editedEntry, setEditedEntry] = useState("");

  function addItem(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: new Date(),
      displayDate: theDate,
      author: currentUser?.email,
      activity: activity,
      active: false,
    });
    setActivity("");
    fetchItems();
  }

  function fetchItems() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);
      setDatabaseItems(items);
    });
  }
  useEffect(() => {
    fetchItems();
  }, []);

  function updateItem(e) {
    console.log("To be UPDATED " + itemToUpdate);
    if (itemToUpdate != "") {
      console.log("Updating");
      const docToUpdate = doc(db, "activities", itemToUpdate);
      updateDoc(docToUpdate, {
        activity: editedEntry,
      }).then(() => {
        console.log("It supposed to be updated");
      });
      setUpdating(false);

      fetchItems();
    }
  }
  async function changeActiveStatus(e) {
    const setItem = await setItemToUpdate(e.id);

    const isActive = await setActiveStatus(!e.active);
  }
  function toggleDone(e) {
    const docToUpdate = doc(db, "activities", itemToUpdate);
    updateDoc(docToUpdate, {
      active: activeStatus,
    });
    fetchItems();
  }

  function deleteItem(e) {
    console.log("To be Deleted " + itemToDelete);
    if (itemToDelete != "") {
      console.log("Deleting");
      const docToDelete = doc(db, "activities", itemToDelete);
      deleteDoc(docToDelete).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchItems();
    }
  }
  return (
    <div id="activitylist">
      <br />
      <br />

      <form className="add">
        <input
          rows="10"
          cols="70"
          value={activity}
          onChange={(e) => setActivity(e.target.value)}
          type="text"
          name="activity"
          required
        />
        <br />
        <button onClick={addItem}>Add activity</button>
      </form>

      <div>
        {" "}
        <h2>Activity List</h2>
        for some reason you have to press the Active/inactive button 3 times
        before it updates, idk why yet
        <div>
          {dataBaseItems.map((database, id) => (
            <div className="insertedItem" key={database.id}>
              <p className="date">{database.displayDate}</p>
              <p> {database.activity}</p>Added by: <i>{database.author}</i>
              <br />
              <button
                onClick={() => {
                  changeActiveStatus(database);
                  toggleDone();
                }}
              >
                {database.active ? (
                  <div className="active-status-button">Done</div>
                ) : (
                  <div className="active-status-button">Not Done</div>
                )}
              </button>
              {updating ? (
                <div>
                  {" "}
                  <p>Change activity to:</p>
                  <textarea
                    value={editedEntry}
                    onChange={(e) => setEditedEntry(e.target.value)}
                  />{" "}
                  <button
                    onClick={(e) => {
                      updateItem();
                    }}
                  >
                    Save
                  </button>
                </div>
              ) : (
                <div></div>
              )}
              <br />
              <button
                onClick={(e) => {
                  setItemToUpdate(database.id);
                  setUpdating(true);
                }}
              >
                Edit
              </button>
              <button
                onClick={(e) => {
                  setItemToDelete(database.id);
                  deleteItem();
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
