import { createElement, useState, useEffect } from "react";
import {
  updateDoc,
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";

export default function CurrentSubScriptions() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "currentSubscriptions");

  const [activeStatus, setActiveStatus] = useState(false);
  const [subscription, setSubscription] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);

  function addSubscription(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: new Date(),
      displayDate: theDate,
      author: currentUser?.email,
      subscription: subscription,
    });
    setSubscription("");
    fetchSubscriptions();
  }

  function fetchSubscriptions() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);

      setDatabaseItems(items);
      dataBaseItems.sort((a, b) => (b.joinDate < a.joinDate ? 1 : -1));
    });
  }
  useEffect(() => {
    fetchSubscriptions();
  }, []);

  function updateSubscription(e) {
    console.log("To be Deleted " + itemToUpdate);
    if (itemToUpdate != "") {
      console.log("Updating");
      const docToUpdate = doc(db, "subscriptions", itemToUpdate);
      updateDoc(docToUpdate, {
        active: activeStatus,
      }).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchSubscriptions();
    }
  }

  function deleteSubscription(e) {
    console.log("To be Deleted " + itemToDelete);
    if (itemToDelete != "") {
      console.log("Deleting");
      const docToDelete = doc(db, "subscriptions", itemToDelete);
      deleteDoc(docToDelete).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchSubscriptions();
    }
  }
  return (
    <div id="subscriptionlist">
      <br />
      <br />

      <form className="add">
        <textarea
          rows="10"
          cols="70"
          value={subscription}
          onChange={(e) => setSubscription(e.target.value)}
          type="text"
          name="subscription"
          required
        />
        <br />
        <button onClick={addSubscription}> Subscription</button>
      </form>

      <div>
        {" "}
        <h2>Subscription List</h2>
        <div>
          {dataBaseItems.map((database, id) => (
            <div className="insertedItem" key={database.id}>
              <p className="date">{database.displayDate}</p>
              <label class="switch">
                <p> Active</p>
                <br />
                <br />
                <input type="checkbox" />
                <span class="slider round"></span>
              </label>
              <p>
                {" "}
                {database.subscription} -<i>{database.author}</i>{" "}
              </p>

              <button
                onClick={(e) => {
                  setItemToDelete(database.id);
                  deleteSubscription();
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
