import { createElement, useState, useEffect } from "react";
import {
  FieldValue,
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";
import { theDate } from "./Utilities/theDate";

export default function RandomPost() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "posts");

  const [author, setAuthor] = useState("");
  const [post, setPost] = useState("");
  const [itemToDelete, setItemToDelete] = useState("");
  const [dataBaseItems, setDatabaseItems] = useState([]);

  function addPost(e) {
    e.preventDefault();

    addDoc(colRef, {
      date: new Date(),
      displayDate: theDate,
      author: currentUser?.email,
      post: post,
    });
    setPost("");
    fetchPosts();
  }

  function fetchPosts() {
    getDocs(colRef).then((snapshot) => {
      const items = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log(items);

      setDatabaseItems(items);
      dataBaseItems.sort((a, b) => (b.joinDate < a.joinDate ? 1 : -1));
    });
  }
  useEffect(() => {
    fetchPosts();
  }, []);

  function deletePost(e) {
    console.log("To be Deleted " + itemToDelete);
    if (itemToDelete != "") {
      console.log("Deleting");
      const docToDelete = doc(db, "posts", itemToDelete);
      deleteDoc(docToDelete).then(() => {
        console.log("It supposed to be deleted");
      });
      fetchPosts();
    }
    // myDb.collection("posts").doc(itemToDelete).delete();
  }
  return (
    <div>
      <br />
      <br />
      <div>Hey: {currentUser?.email} </div>

      <form className="add">
        <textarea
          rows="10"
          cols="70"
          value={post}
          onChange={(e) => setPost(e.target.value)}
          type="text"
          name="post"
          required
        />
        <br />
        <button onClick={addPost}> Post</button>
      </form>

      <div id="postlist">
        {" "}
        <h2>Post List</h2>
        <div id="postlist">
          {dataBaseItems.map((database, id) => (
            <div className="post" key={database.id}>
              <p className="date">{database.displayDate}</p>
              <p>
                {" "}
                {database.post} -<i>{database.author}</i>{" "}
              </p>
              <button
                onClick={(e) => {
                  setItemToDelete(database.id);
                  deletePost();
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
