import { useDispatch } from "react-redux";
import { updateLoginStatus } from "./redux/actions";
import { logout, useAuth } from "./firebase";

export default function DropDownMenue() {
  const dispatch = useDispatch();

  const currentUser = useAuth();

  const isLoggedIn = (isLoggedin) => {
    dispatch(updateLoginStatus(isLoggedin));
  };

  const [loading, setLoading] = useState(false);

  async function handleLogout() {
    setLoading(true);
    try {
      await logout();
      isLoggedIn(false);
    } catch {
      alert("Logout Failed");
    }
    setLoading(false);
  }
  return (
    <div id="main">
      <ul>
        {" "}
        <li className="nav-link">
          <NavLink activestyle={{ color: "red" }} to="/changeProfilePic">
            {" "}
            Change Profile Picture
          </NavLink>
        </li>
        <li className="nav-link">
          <button disabled={loading || !currentUser} onClick={handleLogout}>
            Log Out
          </button>
        </li>
        <li className="nav-link">
          <NavLink activestyle={{ color: "red" }} to="/profile">
            {" "}
            Profile
          </NavLink>
        </li>
      </ul>
    </div>
  );
}
