import React, { Component } from "react";
import "./index.css";
import { NavLink } from "react-router-dom";
import DropDownMenue from "./profilePicDropDown";
import { useRef, useState } from "react";
import { logout, useAuth } from "./firebase";
import { useDispatch } from "react-redux";
import { updateLoginStatus } from "./redux/actions";

export default function NavBar() {
  const dispatch = useDispatch();

  const isLoggedIn = (isLoggedin) => {
    dispatch(updateLoginStatus(isLoggedin));
  };

  const [loading, setLoading] = useState(false);

  const currentUser = useAuth();
  async function handleLogout() {
    setLoading(true);
    try {
      await logout();
      isLoggedIn(false);
    } catch {
      alert("Logout Failed");
    }
    setLoading(false);
  }

  return (
    <div className="navBar">
      {" "}
      <ul>
        <li>
          <NavLink activestyle={{ color: "red" }} to="/">
            {" "}
            Dashboard
          </NavLink>
        </li>
        <li>
          <button disabled={loading || !currentUser} onClick={handleLogout}>
            Log Out
          </button>{" "}
        </li>
        <li className="face">
          <div></div>
          <img
            height="55px"
            src="https://i.ibb.co/KXvpJsB/Screenshot-20211006-142258.png"
            alt="Screenshot-20211006-142258"
            border="0"
          />
        </li>
        <li>
          <div className="search-box">
            <button className="btn-search">
              <i>
                {" "}
                <img
                  height="30px"
                  src="https://i.ibb.co/ZTsJFfR/search-icon-png-9985.png"
                  alt="search-icon-png-9985"
                  border="0"
                />
              </i>
            </button>
            <input
              type="text"
              className="input-search"
              placeholder="Type to Search..."
            />
          </div>
        </li>
      </ul>
    </div>
  );
}
