import { createElement, useState } from "react";
import {
  getFirestore,
  collection,
  getDocs,
  deleteDoc,
  addDoc,
} from "firebase/firestore";
import { useSelector } from "react-redux";
import { useAuth } from "./firebase";

export default function LoggedIn() {
  const db = getFirestore();

  const loginReducer = useSelector((state) => state.loginReducer);
  const { loginStatus } = loginReducer;
  const currentUser = useAuth();

  const colRef = collection(db, "posts");

  const [author, setAuthor] = useState("");
  const [post, setPost] = useState("");
  const postList = [];

  function addPost(e) {
    e.preventDefault();
    addDoc(colRef, {
      author: author,
      post: post,
    });
  }
  getDocs(colRef).then((snapshot) => {
    snapshot.docs.forEach((doc) => {
      postList.push({ ...doc.data(), id: doc.id });

      var z = document.createElement("li"); // is a node
      var v = (innerHTML = JSON.stringify(doc.data()) + ":" + doc.id);

      z.innerHTML = JSON.stringify(doc.data()) + ":" + doc.id;
      document.body.appendChild(z);
    });
    console.log(postList + "hey");
  });

  function deletePost(e) {
    e.preventDefault();
  }
  return (
    <div>
      <body>
        <div>Hey: {currentUser?.email} </div>

        <form className="add">
          <label htmlFor="author">Author:</label>
          <input
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            type="text"
            name="author"
            required
          />
          <label htmlFor="post">Post:</label>
          <input
            value={post}
            onChange={(e) => setPost(e.target.value)}
            type="text"
            name="post"
            required
          />

          <button onClick={addPost}> Post</button>
        </form>
        <form className="delete">
          <label htmlFor="id">Document id:</label>
          <input type="text" name="id" required />

          <button onClick={deletePost}>Delete</button>
        </form>
        <div id="postlist">
          {" "}
          <h2>Post List</h2>
        </div>
      </body>
    </div>
  );
}
